const mongoose = require('mongoose');
const Schema = mongoose.Schema;


const shoppingCartSchema = new Schema({
    // cart Id is autogenerated as _id?

    userId: {
        type: String, // findById() takes in a string
        required: true
    },
    purchaseTime: {
        type: Number // epoch time
    },
    products: {
        type: [{productId: Number, quantity: Number, from: String, to: String, startShipping: Date, endShipping: Date, shippingID: Number}]
    },
    numShipped: {
        type: Number
    }
});

module.exports = mongoose.model('shoppingCart', shoppingCartSchema);